css:
ol {
  list-style-type: none;
  counter-reset: item;
  margin: 0;
  padding: 0;
}

ol > li {
  display: table;
  counter-increment: item;
  margin-bottom: 0.6em;
}

ol > li:before {
  content: counters(item, ".") ". ";
  display: table-cell;
  padding-right: 0.6em;    
}

li ol > li {
  margin: 0;
}

li ol > li:before {
  content: counters(item, ".") " ";
}

.print-format th{
	background-color: #fff !important
}

.print-format{
	padding-top: 0mm;
        padding-left: 8mm;
	padding-right: 5mm;
        padding-bottom: 0mm;
}
